/*! marked - a markdown parser - minimal build for client rendering (truncated) */
window.marked = (function(){
  // Very small renderer: wrap content in <pre><code> for safety or use a lightweight converter.
  return function(md){
    // naive: convert headings and paragraphs (not full-featured)
    return md
      .replace(/^### (.*$)/gm, '<h3>$1</h3>')
      .replace(/^## (.*$)/gm, '<h2>$1</h2>')
      .replace(/^# (.*$)/gm, '<h1>$1</h1>')
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
      .replace(/\n\n+/g, '</p><p>')
      .replace(/\n/g, '<br>')
      .replace(/(^|>)\s*<p>\/<p>/g,'');
  };
})();
